import React, { useContext, useMemo } from "react";
import { useDroppable } from "@dnd-kit/core";
import { SortableContext, useSortable, verticalListSortingStrategy } from "@dnd-kit/sortable";
import { CSS } from "@dnd-kit/utilities";

import { GridDndContext } from "./GridDndContext";
import SortableInstance from "./SortableInstance";

export default function SortableContainer({ container }) {
  const { instances, addInstanceToContainer } = useContext(GridDndContext);

  // container is sortable as an item in the container list
  const {
    setNodeRef,
    attributes,
    listeners,
    transform,
    transition,
    isDragging,
  } = useSortable({
    id: container.id,
    data: { role: "container", label: container.label },
  });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
    opacity: isDragging ? 0.6 : 1,
  };

  // 3 droppables: top, list, bottom
  const top = useDroppable({
    id: `top:${container.id}`,
    data: { role: "container:top", containerId: container.id },
  });

  const list = useDroppable({
    id: `list:${container.id}`,
    data: { role: "container:list", containerId: container.id },
  });

  const bottom = useDroppable({
    id: `bottom:${container.id}`,
    data: { role: "container:bottom", containerId: container.id },
  });

  const instanceMap = useMemo(() => {
    const map = {};
    for (const inst of instances) map[inst.id] = inst;
    return map;
  }, [instances]);

  const items = container.items.map((id) => instanceMap[id]).filter(Boolean);
  const itemIds = items.map((x) => x.id);

  return (
    <div ref={setNodeRef} style={style} className="container-shell">
      {/* Top drop zone */}
      <div
        ref={top.setNodeRef}
        className={"container-sentinel" + (top.isOver ? " over" : "")}
      />

      {/* Header (drag handle is the label area) */}
      <div className="container-header" {...attributes} {...listeners}>
        <div style={{ fontWeight: 600 }}>{container.label}</div>
        <button onClick={() => addInstanceToContainer(container.id)}>+ Instance</button>
      </div>

      {/* List drop zone + sortable instances */}
      <div
        ref={list.setNodeRef}
        className={"container-list" + (list.isOver ? " over" : "")}
      >
        <SortableContext items={itemIds} strategy={verticalListSortingStrategy}>
          {items.map((inst) => (
            <SortableInstance key={inst.id} instance={inst} containerId={container.id} />
          ))}
        </SortableContext>

        {items.length === 0 && (
          <div style={{ fontSize: 12, opacity: 0.6, fontStyle: "italic" }}>
            Drop items here
          </div>
        )}
      </div>

      {/* Bottom drop zone */}
      <div
        ref={bottom.setNodeRef}
        className={"container-sentinel" + (bottom.isOver ? " over" : "")}
      />
    </div>
  );
}
