import React, { useContext } from "react";
import { GridDataContext } from "./GridDataContext";
import MoreVerticalIcon from "@atlaskit/icon/glyph/more-vertical";

function InstanceInner({
  id,
  label,
  overlay = false,
  dragHandleRef,
  dragAttributes,
  dragListeners,
}) {
  const { activeId } = useContext(GridDataContext);
  const isOriginalActive = !overlay && activeId === id;

  return (
    <div
      className={"dnd-instance" + (isOriginalActive ? " hidden" : "")}
      style={{
        // ✅ IMPORTANT: allow scroll gestures on the row
        touchAction: "pan-y",
        WebkitUserSelect: "none",
        userSelect: "none",
        display: "flex",
        alignItems: "center",
        gap: 8,
      }}
    >
      {/* ✅ DRAG HANDLE ONLY */}
      {!overlay && (
        <div
          ref={dragHandleRef}
          {...dragAttributes}
          {...dragListeners}
          style={{
            touchAction: "none",      // ✅ only handle disables gestures
            cursor: "grab",
            padding: "6px 4px",
            display: "flex",
            alignItems: "center",
          }}
        >
          <MoreVerticalIcon size="small" primaryColor="#9AA0A6" />
        </div>
      )}

      <div style={{ flex: 1, minWidth: 0 }}>
        {label}
      </div>
    </div>
  );
}

export default React.memo(InstanceInner);
