import React, { useContext, useMemo } from "react";
import { GridDndContext } from "./GridDndContext";
import { useSortable } from "@dnd-kit/sortable";
import { CSS } from "@dnd-kit/utilities";
import Instance from "./Instance";

export default function SortableInstance({ instance, containerId }) {
      const { useRenderCount } = useContext(GridDndContext);

  const { setNodeRef, attributes, listeners, transform, transition, isDragging } =
    useSortable({
      id: instance.id,
      data: { role: "instance", containerId, label: instance.label },
    });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
    opacity: isDragging ? 0.6 : 1,
  };
useRenderCount(`SortableInstance ${instance.id}`);

  return (
    <div ref={setNodeRef} style={style}>
      <Instance
        id={instance.id}
        label={instance.label}
        dragAttributes={attributes}
        dragListeners={listeners}
      />
    </div>
  );
}
