// Grid.jsx
import React, { useContext, useMemo } from "react";
import {
  DndContext,
  DragOverlay,
  PointerSensor,
  useSensor,
  useSensors,
} from "@dnd-kit/core";
import { SortableContext, verticalListSortingStrategy } from "@dnd-kit/sortable";

import { GridDndContext } from "./GridDndContext";
import SortableContainer from "./SortableContainer";
import Instance from "./Instance";

export default function Grid() {
  const {
    containersRender,
    instances,
    activeId,
    activeSize,
    handleDragStart,
    handleDragOver,     // ✅
    handleDragEnd,
    handleDragCancel,
  } = useContext(GridDndContext);

  const sensors = useSensors(
    useSensor(PointerSensor, { activationConstraint: { distance: 5 } })
  );

  const containerIds = useMemo(
    () => containersRender.map((c) => c.id),
    [containersRender]
  );

  const activeInstance = activeId ? instances.find((x) => x.id === activeId) : null;

  return (
    <DndContext
      sensors={sensors}
      onDragStart={handleDragStart}
      onDragOver={handleDragOver}     // ✅ soft-sort
      onDragEnd={handleDragEnd}       // ✅ commit
      onDragCancel={handleDragCancel} // ✅ revert
    >
      <div className="containers-col">
        <SortableContext items={containerIds} strategy={verticalListSortingStrategy}>
          {containersRender.map((c) => (
            <SortableContainer key={c.id} container={c} />
          ))}
        </SortableContext>
      </div>

      <DragOverlay adjustScale={false}>
        {activeId ? (
          <div style={{ width: activeSize?.width, height: activeSize?.height }}>
            <Instance
              id={`overlay-${activeId}`}
              label={activeInstance?.label ?? "Dragging"}
              overlay
            />
          </div>
        ) : null}
      </DragOverlay>
    </DndContext>
  );
}
