import React, { useContext } from "react";
import { useDroppable } from "@dnd-kit/core";
import { GridDndContext } from "./GridDndContext";
import Instance from "./Instance";

export default function Container({ container }) {
  const { instances, addInstance } = useContext(GridDndContext);

  const { setNodeRef, isOver } = useDroppable({
    id: container.id,
  });

  const items = instances.filter((x) => x.containerId === container.id);

  return (
    <div
      ref={setNodeRef}
      className={isOver ? "dnd-container dnd-container--over" : "dnd-container"}
    >
      <div className="dnd-container__label" style={{ display: "flex", gap: 8 }}>
        <span style={{ flex: 1 }}>{container.label}</span>
        <button onClick={() => addInstance(container.id)}>+ Instance</button>
      </div>

      {items.map((inst) => (
        <Instance key={inst.id} id={inst.id} label={inst.label} />
      ))}

      {items.length === 0 && (
        <div style={{ fontSize: 12, opacity: 0.5, fontStyle: "italic" }}>
          Drop here
        </div>
      )}
    </div>
  );
}
