// state/actions.js

export const ActionTypes = {
  // old-style hydration
  HYDRATE: "HYDRATE",
  FULL_STATE: "FULL_STATE",

  SET_GRID_ID: "SET_GRID_ID",
  SET_USER_ID: "SET_USER_ID",

  PATCH_GRID: "PATCH_GRID",
  PATCH_PANEL: "PATCH_PANEL",

  // your new container/instance actions
  ADD_CONTAINER: "ADD_CONTAINER",
  ADD_INSTANCE_TO_CONTAINER: "ADD_INSTANCE_TO_CONTAINER",

  SET_ACTIVE_ID: "SET_ACTIVE_ID",
  SET_ACTIVE_SIZE: "SET_ACTIVE_SIZE",
  SET_DEBUG_EVENT: "SET_DEBUG_EVENT",

  SET_CONTAINERS: "SET_CONTAINERS",
  SET_INSTANCES: "SET_INSTANCES",

  PATCH_CONTAINER_ITEMS: "PATCH_CONTAINER_ITEMS",
  PATCH_INSTANCE: "PATCH_INSTANCE",

  SOFT_TICK: "SOFT_TICK",
};

// ---- old-style creators you asked for ----
export const hydrate = (payload) => ({ type: ActionTypes.HYDRATE, payload });
export const fullState = (payload) => ({ type: ActionTypes.FULL_STATE, payload });
export const setGridId = (gridId) => ({ type: ActionTypes.SET_GRID_ID, payload: gridId });
export const setUserId = (userId) => ({ type: ActionTypes.SET_USER_ID, payload: userId });

export const patchGrid = (grid) => ({ type: ActionTypes.PATCH_GRID, payload: { grid } });
export const patchPanel = (panel) => ({ type: ActionTypes.PATCH_PANEL, payload: { panel } });

// ---- keep your new creators ----
export const addContainerAction = ({ id, label }) => ({
  type: ActionTypes.ADD_CONTAINER,
  payload: { id, label },
});

export const addInstanceToContainerAction = ({ containerId, instance }) => ({
  type: ActionTypes.ADD_INSTANCE_TO_CONTAINER,
  payload: { containerId, instance },
});

export const setActiveIdAction = (activeId) => ({
  type: ActionTypes.SET_ACTIVE_ID,
  payload: { activeId },
});

export const setActiveSizeAction = (activeSize) => ({
  type: ActionTypes.SET_ACTIVE_SIZE,
  payload: { activeSize },
});

export const setDebugEventAction = (debugEvent) => ({
  type: ActionTypes.SET_DEBUG_EVENT,
  payload: { debugEvent },
});

export const setContainersAction = (containers) => ({
  type: ActionTypes.SET_CONTAINERS,
  payload: { containers },
});

export const setInstancesAction = (instances) => ({
  type: ActionTypes.SET_INSTANCES,
  payload: { instances },
});

export const patchContainerItemsAction = ({ containerId, items }) => ({
  type: ActionTypes.PATCH_CONTAINER_ITEMS,
  payload: { containerId, items },
});

export const patchInstanceAction = (instance) => ({
  type: ActionTypes.PATCH_INSTANCE,
  payload: { instance },
});

export const softTickAction = () => ({ type: ActionTypes.SOFT_TICK });
