import React, { useMemo, useState } from "react";
import Grid from "./Grid";
import Container from "./Container";
import { GridDndContext } from "./GridDndContext";

function uid() {
  // works in modern browsers; safe fallback for termux/weird envs
  return (globalThis.crypto?.randomUUID?.() ||
    `${Date.now()}-${Math.random().toString(16).slice(2)}`);
}

export default function App() {
  // DnD shared state
  const [activeId, setActiveId] = useState(null);
  const [activeSize, setActiveSize] = useState(null);

  // ✅ real data state (start empty)
  const [containers, setContainers] = useState([]);
  const [instances, setInstances] = useState([]);

  // helpers
  const addContainer = () => {
    const id = uid();
    setContainers((prev) => [
      ...prev,
      { id, label: `Container ${prev.length + 1}` },
    ]);
  };

  const addInstance = (containerId) => {
    const id = uid();
    setInstances((prev) => [
      ...prev,
      { id, containerId, label: `Item ${prev.length + 1}` },
    ]);
  };

  const moveInstance = (instanceId, toContainerId) => {
    setInstances((prev) =>
      prev.map((inst) =>
        inst.id === instanceId ? { ...inst, containerId: toContainerId } : inst
      )
    );
  };

  const ctxValue = useMemo(
    () => ({
      activeId,
      activeSize,
      setActiveId,
      setActiveSize,

      containers,
      setContainers,
      instances,
      setInstances,

      addContainer,
      addInstance,
      moveInstance,
    }),
    [activeId, activeSize, containers, instances]
  );

  return (
    <GridDndContext.Provider value={ctxValue}>
      <div className="app-root">
        <div className="dnd-page">
          <div style={{ display: "flex", gap: 12, alignItems: "center" }}>
            <h2 style={{ margin: 0 }}>Basic dnd-kit Example</h2>
            <button onClick={addContainer}>+ Create Container</button>
          </div>

          <Grid>
            <div className="containers-row">
              {containers.map((c) => (
                <Container key={c.id} container={c} />
              ))}
            </div>
          </Grid>

          {containers.length === 0 && (
            <div style={{ marginTop: 12, opacity: 0.7, fontSize: 13 }}>
              Create a container to get started.
            </div>
          )}
        </div>
      </div>
    </GridDndContext.Provider>
  );
}
