// Instance.jsx
import React, { useContext } from "react";
import { GridDndContext } from "./GridDndContext";

export default function Instance({
  id,
  label,
  overlay = false,
  dragAttributes,
  dragListeners,
}) {
  const { activeId } = useContext(GridDndContext);

  const isOriginalActive = !overlay && activeId === id;

  return (
    <div
      className={"dnd-instance" + (isOriginalActive ? " hidden" : "")}
      {...(!overlay ? dragAttributes : {})}
      {...(!overlay ? dragListeners : {})}
    >
      {label}
    </div>
  );
}
