import { createContext } from "react";

export const GridDndContext = createContext({
  // drag state
  activeId: null,
  activeSize: null,
  setActiveId: () => {},
  setActiveSize: () => {},

  // data state
  containers: [],
  setContainers: () => {},
  instances: [],
  setInstances: () => {},

  // helpers (optional but clean)
  addContainer: () => {},
  addInstance: () => {},
  moveInstance: () => {},
});
