// App.js
import React, { useMemo, useRef, useState } from "react";
import Grid from "./Grid";
import { GridDndContext } from "./GridDndContext";

function uid() {
  return Date.now().toString(36) + Math.random().toString(36).slice(2, 10);
}
function DebugBar({
  containers,
  containersDraft,
  activeId,
}) {
  return (
    <div
      style={{
        position: "sticky",
        top: 0,
        zIndex: 999,
        background: "#0e1116",
        borderBottom: "1px solid #222",
        padding: "8px 12px",
        fontSize: 12,
        fontFamily: "monospace",
        color: "#9aa4b2",
        display: "flex",
        gap: 16,
        flexWrap: "wrap",
      }}
    >
      <div>
        <strong>activeId:</strong>{" "}
        <span style={{ color: "#fff" }}>{activeId ?? "null"}</span>
      </div>

      <div>
        <strong>containers (state):</strong>{" "}
        {containers.length}
      </div>

      <div>
        <strong>draftRef:</strong>{" "}
        <span style={{ color: containersDraft ? "#4caf50" : "#f44336" }}>
          {containersDraft ? "ACTIVE" : "null"}
        </span>
      </div>

      <details style={{ cursor: "pointer" }}>
        <summary>state</summary>
        <pre style={{ whiteSpace: "pre-wrap", maxWidth: 600 }}>
          {JSON.stringify(containers, null, 2)}
        </pre>
      </details>

      <details style={{ cursor: "pointer" }}>
        <summary>draft</summary>
        <pre style={{ whiteSpace: "pre-wrap", maxWidth: 600 }}>
          {JSON.stringify(containersDraft, null, 2)}
        </pre>
      </details>
    </div>
  );
}

function arrayMove(arr, from, to) {
  const copy = [...arr];
  const [item] = copy.splice(from, 1);
  copy.splice(to, 0, item);
  return copy;
}

function deepCloneContainers(containers) {
  // simple + safe for this shape
  return containers.map((c) => ({ ...c, items: [...c.items] }));
}

export default function App() {
  // drag state
  const [activeId, setActiveId] = useState(null);
  const [activeSize, setActiveSize] = useState(null);

  // data state
  const [containers, setContainers] = useState([]); // {id,label,items:[]}
  const [instances, setInstances] = useState([]);   // {id,label}

  // ✅ soft-sort draft
  const containersDraftRef = useRef(null);
  const [softTick, setSoftTick] = useState(0); // forces rerender while ref changes

  // creators
  const addContainer = () => {
    const id = uid();
    setContainers((prev) => [...prev, { id, label: `List ${prev.length + 1}`, items: [] }]);
  };

  const addInstanceToContainer = (containerId) => {
    const instanceId = uid();
    setInstances((prev) => [...prev, { id: instanceId, label: `Item ${prev.length + 1}` }]);

    setContainers((prev) =>
      prev.map((c) => (c.id === containerId ? { ...c, items: [...c.items, instanceId] } : c))
    );
  };

  // helpers (operate on a given list)
  const findContainerIndexById = (id, list) => list.findIndex((c) => c.id === id);
  const findContainerByInstanceId = (instanceId, list) => list.find((c) => c.items.includes(instanceId));

  const getWorkingContainers = () => containersDraftRef.current ?? containers;

  // -------------------------
  // Drag Handlers (App-owned)
  // -------------------------
  const handleDragStart = (event) => {
    setActiveId(event.active.id);

    const rect = event.active.rect?.current?.initial;
    if (rect) setActiveSize({ width: rect.width, height: rect.height });

    // ✅ create soft draft snapshot
    containersDraftRef.current = deepCloneContainers(containers);
  };

  const handleDragCancel = () => {
    setActiveId(null);
    setActiveSize(null);
    containersDraftRef.current = null;
    setSoftTick((t) => t + 1);
  };

  // ✅ soft-sort onDragOver (mutate ref only)
  const handleDragOver = (event) => {
    const { active, over } = event;
    if (!over) return;

    const draft = containersDraftRef.current;
    if (!draft) return;

    const activeRole = active.data.current?.role;
    const overRole = over.data.current?.role;

    // -------------------------
    // A) Dragging a CONTAINER
    // -------------------------
    if (activeRole === "container") {
      const fromIndex = findContainerIndexById(active.id, draft);
      const targetContainerId = over.data.current?.containerId ?? over.id;
      const targetIndex = findContainerIndexById(targetContainerId, draft);

      if (fromIndex === -1 || targetIndex === -1) return;
      if (fromIndex === targetIndex) return;

      let toIndex = targetIndex;

      // 3-zone behavior
      if (overRole === "container:top") toIndex = targetIndex;
      else if (overRole === "container:bottom") toIndex = targetIndex + 1;
      else toIndex = targetIndex; // container:list

      const movingForward = fromIndex < toIndex;
      const adjustedTo = movingForward ? toIndex - 1 : toIndex;

      const clamped = Math.max(0, Math.min(draft.length - 1, adjustedTo));

      containersDraftRef.current = arrayMove(draft, fromIndex, clamped);
      setSoftTick((t) => t + 1);
      return;
    }

    // -------------------------
    // B) Dragging an INSTANCE (sortable + cross-container)
    // -------------------------
    if (activeRole === "instance") {
      const instanceId = active.id;

      const fromContainer = findContainerByInstanceId(instanceId, draft);
      if (!fromContainer) return;

      // where is it going?
      const toContainerId = over.data.current?.containerId;
      if (!toContainerId) return;

      const toContainer = draft.find((c) => c.id === toContainerId);
      if (!toContainer) return;

      // compute insertion index
      const overInstanceId = overRole === "instance" ? over.id : null;

      // remove from source
      fromContainer.items = fromContainer.items.filter((id) => id !== instanceId);

      // insert into destination
      if (!overInstanceId) {
        // dropped on list area -> append
        toContainer.items.push(instanceId);
      } else {
        const idx = toContainer.items.indexOf(overInstanceId);
        const insertAt = idx >= 0 ? idx : toContainer.items.length;
        toContainer.items.splice(insertAt, 0, instanceId);
      }

      setSoftTick((t) => t + 1);
      return;
    }
  };

  // ✅ commit onDragEnd
  const handleDragEnd = (event) => {
    const { over } = event;

    setActiveId(null);
    setActiveSize(null);

    // if no drop target, revert
    if (!over) {
      containersDraftRef.current = null;
      setSoftTick((t) => t + 1);
      return;
    }

    // commit draft if present
    if (containersDraftRef.current) {
      setContainers(containersDraftRef.current);
      containersDraftRef.current = null;
      setSoftTick((t) => t + 1);
    }
  };

  // render list = draft if dragging, else real state
  const containersRender = getWorkingContainers();

  const ctxValue = useMemo(
    () => ({
      activeId,
      activeSize,
      setActiveId,
      setActiveSize,

      containers,
      containersRender, // ✅
      setContainers,

      instances,
      setInstances,

      addContainer,
      addInstanceToContainer,

      handleDragStart,
      handleDragOver,   // ✅
      handleDragEnd,
      handleDragCancel,

      // include softTick so memo updates while draft mutates
      softTick,
    }),
    [activeId, activeSize, containers, containersRender, instances, softTick]
  );

  return (
    <GridDndContext.Provider value={ctxValue}>
      <div className="app-root">
        <div className="test">

        </div>
        <div className="dnd-page">
          <div style={{ display: "flex", gap: 12, alignItems: "center" }}>
            <h2 style={{ margin: 0 }}>Sortable Containers + Instances</h2>
            <button onClick={addContainer}>+ Container</button>
          </div>

          <Grid />

          {containers.length === 0 && (
            <div style={{ marginTop: 12, opacity: 0.7, fontSize: 13 }}>
              Create a container to start.
            </div>
          )}
        </div>
      </div>
    </GridDndContext.Provider>
  );
}
