// Grid.jsx
import React, { useContext } from "react";
import {
  DndContext,
  DragOverlay,
  PointerSensor,
  useSensor,
  useSensors,
} from "@dnd-kit/core";

import { GridDndContext } from "./GridDndContext";
import Instance from "./Instance";

export default function Grid({ children }) {
  const {
    activeId,
    activeSize,
    instances,
    handleDragStart,
    handleDragEnd,
    handleDragCancel,
  } = useContext(GridDndContext);

  const sensors = useSensors(
    useSensor(PointerSensor, {
      activationConstraint: { distance: 5 },
    })
  );

  const activeInst = activeId ? instances.find((x) => x.id === activeId) : null;

  return (
    <DndContext
      sensors={sensors}
      onDragStart={handleDragStart}
      onDragEnd={handleDragEnd}
      onDragCancel={handleDragCancel}
    >
      {children}

      <DragOverlay adjustScale={false}>
        {activeId ? (
          <div style={{ width: activeSize?.width, height: activeSize?.height }}>
            <Instance
              id={`overlay-${activeId}`}
              label={activeInst?.label ?? "Dragging"}
              overlay
            />
          </div>
        ) : null}
      </DragOverlay>
    </DndContext>
  );
}
