// Instance.jsx
import React, { useContext } from "react";
import { useDraggable } from "@dnd-kit/core";
import { CSS } from "@dnd-kit/utilities";
import { GridDndContext } from "./GridDndContext";

export default function Instance({ id, label, overlay = false }) {
  const { activeId } = useContext(GridDndContext);

  const { attributes, listeners, setNodeRef, transform, isDragging } =
    useDraggable({ id, disabled: overlay });

  const isOriginalActive = !overlay && activeId === id;

  const style = overlay
    ? {}
    : {
        transform: CSS.Transform.toString(transform),
        opacity: isOriginalActive ? 0 : 1, // hide original while overlay shows
      };

  return (
    <div
      ref={overlay ? null : setNodeRef}
      className={
        "dnd-instance" + (isDragging ? " dnd-instance--dragging" : "")
      }
      style={style}
      {...(!overlay ? listeners : {})}
      {...(!overlay ? attributes : {})}
    >
      {label}
    </div>
  );
}
