import React, { useEffect, useMemo, useState } from "react";
import Textfield from "@atlaskit/textfield";
import Button from "@atlaskit/button";
import { Label } from "@atlaskit/form";

import { socket } from "./socket";
import { bindSocketToStore } from "./state/bindSocketToStore";
import { ActionTypes } from "./state/actions";

import Grid from "./Grid";
import LoginScreen from "./LoginScreen";

import { GridDataContext } from "./GridDataContext";
import { GridActionsContext } from "./GridActionsContext";

import { useBoardState } from "./state/useBoardState";
import { useDndReorderCoordinator } from "./helpers/useDndReorderCoordinator";

function useRenderCount(label) {
  const ref = React.useRef(0);
  ref.current += 1;
  console.log(`${label} render #${ref.current}`);
}

function DebugBar({ containers, containersDraft, activeId, debugEvent }) {
  return (
    <div
      style={{
        display: NONAME,
        position: "relative",
        top: 0,
        zIndex: 999,
        background: "#0e1116",
        borderBottom: "1px solid #222",
        padding: "8px 12px",
        fontSize: 12,
        fontFamily: "monospace",
        color: "#9aa4b2",
        display: "flex",
        gap: 16,
        flexWrap: "wrap",
      }}
      className="debugbar"
    >
      <div>
        <strong>activeId:</strong>{" "}
        <span style={{ color: "#fff" }}>{activeId ?? "null"}</span>
      </div>

      <div>
        <strong>debugEvent:</strong>{" "}
        <span style={{ color: "#fff" }}>{debugEvent?.type ?? "null"}</span>
      </div>

      <div>
        <strong>containers (state):</strong> {containers.length}
      </div>

      <div>
        <strong>draftRef:</strong>{" "}
        <span style={{ color: containersDraft ? "#4caf50" : "#f44336" }}>
          {containersDraft ? "ACTIVE" : "null"}
        </span>
      </div>

      <details style={{ cursor: "pointer" }}>
        <summary>event</summary>
        <pre style={{ whiteSpace: "pre-wrap", maxWidth: 700 }}>
          {JSON.stringify(debugEvent, null, 2)}
        </pre>
      </details>

      <details style={{ cursor: "pointer" }}>
        <summary>state</summary>
        <pre style={{ whiteSpace: "pre-wrap", maxWidth: 700 }}>
          {JSON.stringify(containers, null, 2)}
        </pre>
      </details>

      <details style={{ cursor: "pointer" }}>
        <summary>draft</summary>
        <pre style={{ whiteSpace: "pre-wrap", maxWidth: 700 }}>
          {JSON.stringify(containersDraft, null, 2)}
        </pre>
      </details>
    </div>
  );
}

function ToolbarBar({
  show,
  onClose,

  gridId,
  availableGrids,

  gridName,
  setGridName,
  rowInput,
  setRowInput,
  colInput,
  setColInput,

  onGridChange,
  onCreateNewGrid,
  onCommitGridName,
  onUpdateRows,
  onUpdateCols,
  onAddPanel,
}) {
  return (
    <div
      style={{
        position: "relative",
        top: 0,
        zIndex: 998,
        background: "#0e1116",
        borderBottom: "1px solid #222",
        padding: "8px 12px",
        fontSize: 12,
        fontFamily: "monospace",
        color: "#9aa4b2",
        display: show ? "flex" : "none",
        gap: 16,
        flexWrap: "wrap",
        alignItems: "center",
      }}
      className="toolbarbar"
    >
      {/* Grid select + New Grid */}
      <div style={{ display: "flex", alignItems: "center", gap: 8 }}>
        <strong style={{ color: "#fff" }}>Grid</strong>

        <select
          value={gridId || ""}
          onChange={onGridChange}
          style={{
            background: "#0b0f14",
            color: "white",
            border: "1px solid #222",
            borderRadius: 4,
            padding: "4px 8px",
            fontSize: 12,
            fontFamily: "monospace",
          }}
        >
          {(availableGrids || []).map((g) => (
            <option key={g.id} value={g.id}>
              {g.name || `Grid ${String(g.id).slice(-4)}`}
            </option>
          ))}
        </select>

        <Button appearance="default" onClick={onCreateNewGrid}>
          New Grid
        </Button>
      </div>

      {/* Grid name + rows/cols */}
      <div style={{ display: "flex", alignItems: "end", gap: 10 }}>
        <div style={{ display: "flex", flexDirection: "column" }}>
          <Label htmlFor="grid_name" style={{ color: "#9aa4b2", fontSize: 11 }}>
            Grid Name
          </Label>
          <Textfield
            id="grid_name"
            value={gridName ?? ""}
            onChange={(e) => setGridName(e.target.value)}
            onKeyDown={(e) => {
              if (e.key === "Enter") {
                e.preventDefault();
                onCommitGridName?.();
                e.currentTarget.blur();
              }
            }}
            style={{
              background: "#0b0f14",
              color: "white",
              border: "1px solid #222",
              minWidth: 220,
              fontFamily: "monospace",
            }}
          />
        </div>

        <div style={{ display: "flex", flexDirection: "column", width: 72 }}>
          <Label htmlFor="grid_row" style={{ color: "#9aa4b2", fontSize: 11 }}>
            Rows
          </Label>
          <Textfield
            id="grid_row"
            type="number"
            value={rowInput}
            onChange={(e) => {
              const val = e.target.value;
              setRowInput(val);
              if (val === "") return;
              onUpdateRows?.(val);
            }}
            onBlur={() => {
              if (rowInput === "") {
                setRowInput("1");
                onUpdateRows?.("1");
              }
            }}
            style={{
              background: "#0b0f14",
              color: "white",
              border: "1px solid #222",
              fontFamily: "monospace",
            }}
          />
        </div>

        <div style={{ display: "flex", flexDirection: "column", width: 72 }}>
          <Label htmlFor="grid_col" style={{ color: "#9aa4b2", fontSize: 11 }}>
            Cols
          </Label>
          <Textfield
            id="grid_col"
            type="number"
            value={colInput}
            onChange={(e) => {
              const val = e.target.value;
              setColInput(val);
              if (val === "") return;
              onUpdateCols?.(val);
            }}
            onBlur={() => {
              if (colInput === "") {
                setColInput("1");
                onUpdateCols?.("1");
              }
            }}
            style={{
              background: "#0b0f14",
              color: "white",
              border: "1px solid #222",
              fontFamily: "monospace",
            }}
          />
        </div>
      </div>

      {/* Actions */}
      <div style={{ display: "flex", alignItems: "center", gap: 10 }}>
        <Button appearance="primary" onClick={onAddPanel}>
          Add Panel
        </Button>

        <Button appearance="warning" onClick={onClose}>
          Close
        </Button>
      </div>
    </div>
  );
}

function findNextOpenPosition(panels = [], rows = 1, cols = 1) {
  const taken = new Set(panels.map((p) => `${p.row}-${p.col}`));
  for (let r = 0; r < rows; r++) {
    for (let c = 0; c < cols; c++) {
      const key = `${r}-${c}`;
      if (!taken.has(key)) return { row: r, col: c };
    }
  }
  return { row: 0, col: 0 };
}

export default function App() {
  const { state, dispatch } = useBoardState();

  const {
    addContainer,
    addInstanceToContainer,
    handleDragStart,
    handleDragOver,
    handleDragEnd,
    handleDragCancel,
    containersDraftRef,
    getWorkingContainers,
  } = useDndReorderCoordinator({ state, dispatch, socket });

  const containersRender = getWorkingContainers();

  // -----------------------------
  // TOOLBAR LOCAL UI STATE
  // -----------------------------
  const [showToolbar, setShowToolbar] = useState(false);
  const [gridName, setGridName] = useState("");
  const [rowInput, setRowInput] = useState("1");
  const [colInput, setColInput] = useState("1");

  // Sync toolbar fields from hydrated grid
  useEffect(() => {
    if (state?.grid) {
      setGridName(state.grid.name || "");
      setRowInput(String(state.grid.rows ?? 1));
      setColInput(String(state.grid.cols ?? 1));
    } else {
      setGridName("");
      setRowInput("1");
      setColInput("1");
    }
  }, [state?.grid?._id, state?.grid?.rows, state?.grid?.cols]);

  // -----------------------------
  // SOCKET BIND + HYDRATE
  // -----------------------------
  useEffect(() => {
    const unbind = bindSocketToStore(socket, dispatch);

    const savedUserId = localStorage.getItem("moduli-userId");
    const savedGridId = localStorage.getItem("moduli-gridId");

    if (savedUserId) {
      if (savedGridId) socket.emit("request_full_state", { gridId: savedGridId });
      else socket.emit("request_full_state");
    }

    return () => unbind?.();
  }, [dispatch]);

  // -----------------------------
  // TOOLBAR HANDLERS (REAL EMITS)
  // -----------------------------
  const handleGridChange = (e) => {
    const newGridId = e.target.value;
    if (!newGridId || newGridId === state.gridId) return;

    localStorage.setItem("moduli-gridId", newGridId);
    socket.emit("request_full_state", { gridId: newGridId });
  };

  const handleCreateNewGrid = () => {
    localStorage.removeItem("moduli-gridId");
    socket.emit("request_full_state"); // server creates new grid
  };

  const commitGridName = () => {
    if (!state.gridId) return;

    const trimmed = (gridName || "").trim();
    if (!trimmed) return;

    // optimistic local patch (keeps UI snappy)
    dispatch({
      type: ActionTypes.PATCH_GRID,
      payload: { grid: { name: trimmed } },
    });

    // server patch
    socket.emit("update_grid", { gridId: state.gridId, name: trimmed });
  };

  const updateRows = (val) => {
    if (!state.gridId) return;
    const num = Math.max(1, Number(val) || 1);

    dispatch({
      type: ActionTypes.PATCH_GRID,
      payload: { grid: { rows: num } },
    });

    socket.emit("update_grid", { gridId: state.gridId, rows: num });
  };

  const updateCols = (val) => {
    if (!state.gridId) return;
    const num = Math.max(1, Number(val) || 1);

    dispatch({
      type: ActionTypes.PATCH_GRID,
      payload: { grid: { cols: num } },
    });

    socket.emit("update_grid", { gridId: state.gridId, cols: num });
  };

  const addNewPanel = () => {
    if (!state.gridId || !state.grid) return;

    const panelId = crypto.randomUUID();
    const containerId = `taskbox-${panelId}`;

    const { row, col } = findNextOpenPosition(
      state.panels || [],
      state.grid.rows ?? 1,
      state.grid.cols ?? 1
    );

    // NOTE: panels are userId-only on server, but still include gridId in panel object if you want.
    const panel = {
      id: panelId,
      role: "panel",
      type: "taskbox",
      containerId,
      props: { containerId },
      row,
      col,
      width: 1,
      height: 1,
      gridId: state.gridId,
    };

    // optimistic local add
    dispatch({ type: ActionTypes.PATCH_PANEL, payload: { panel } });

    // tell server to upsert + broadcast panel_updated
    socket.emit("add_panel", { panel });

    // also ensure container exists for that panel (empty list)
    // optimistic: add container if missing
    if (!(state.containers || []).some((c) => c.id === containerId)) {
      dispatch({
        type: ActionTypes.ADD_CONTAINER,
        payload: { id: containerId, label: "TaskBox", },
      });

      socket.emit("create_container", {
        container: { id: containerId, label: "TaskBox" },
      });
    }

    // ensure items are empty (both local + server)
    dispatch({
      type: ActionTypes.PATCH_CONTAINER_ITEMS,
      payload: { containerId, items: [] },
    });
    socket.emit("update_container_items", { containerId, items: [] });
  };

  // -----------------------------
  // CONTEXT VALUES
  // -----------------------------
  const dataValue = useMemo(
    () => ({
      activeId: state.activeId,
      activeSize: state.activeSize,
      debugEvent: state.debugEvent,

      containers: state.containers,
      containersRender,
      instances: state.instances,

      softTick: state.softTick,

      userId: state.userId,
      gridId: state.gridId,
      grid: state.grid,
      panels: state.panels,
      availableGrids: state.availableGrids,
      hydrated: state.hydrated,
    }),
    [
      state.activeId,
      state.activeSize,
      state.debugEvent,
      state.containers,
      containersRender,
      state.instances,
      state.softTick,
      state.userId,
      state.gridId,
      state.grid,
      state.panels,
      state.availableGrids,
      state.hydrated,
    ]
  );

  const actionsValue = useMemo(
    () => ({
      addContainer,
      addInstanceToContainer,
      handleDragStart,
      handleDragOver,
      handleDragEnd,
      handleDragCancel,
      useRenderCount,

      toggleToolbar: () => setShowToolbar(true),
      hideToolbar: () => setShowToolbar(false),
    }),
    [
      addContainer,
      addInstanceToContainer,
      handleDragStart,
      handleDragOver,
      handleDragEnd,
      handleDragCancel,
    ]
  );

  useRenderCount("App");

  // -----------------------------
  // LOGIN GATE
  // -----------------------------
  const savedUserId = localStorage.getItem("moduli-userId");
  if (!savedUserId) return <LoginScreen />;

  return (
    <GridActionsContext.Provider value={actionsValue}>
      <GridDataContext.Provider value={dataValue}>
        <ToolbarBar
          show={showToolbar}
          onClose={() => setShowToolbar(false)}
          gridId={state.gridId}
          availableGrids={state.availableGrids || []}
          gridName={gridName}
          setGridName={setGridName}
          rowInput={rowInput}
          setRowInput={setRowInput}
          colInput={colInput}
          setColInput={setColInput}
          onGridChange={handleGridChange}
          onCreateNewGrid={handleCreateNewGrid}
          onCommitGridName={commitGridName}
          onUpdateRows={updateRows}
          onUpdateCols={updateCols}
          onAddPanel={addNewPanel}
        />

        <DebugBar
          containers={state.containers}
          containersDraft={containersDraftRef.current}
          activeId={state.activeId}
          debugEvent={state.debugEvent}
        />

        <div className="app-root">
          <div className="dnd-page">
            <div style={{ display: "flex", gap: 12, alignItems: "center" }}>
              <h2 style={{ margin: 0 }}>Sortable Containers + Instances</h2>
              <button onClick={addContainer}>+ Container</button>
              <button onClick={() => setShowToolbar((v) => !v)}>
                {showToolbar ? "Hide Toolbar" : "Show Toolbar"}
              </button>
            </div>

            <Grid />

            {state.containers.length === 0 && (
              <div style={{ marginTop: 12, opacity: 0.7, fontSize: 13 }}>
                Create a container to start.
              </div>
            )}
          </div>
        </div>
      </GridDataContext.Provider>
    </GridActionsContext.Provider>
  );
}
