// GridDndContext.js
import { createContext } from "react";

export const GridDndContext = createContext({
  // drag state
  activeId: null,
  activeSize: null,
  setActiveId: () => {},
  setActiveSize: () => {},

  // data state
  containers: [],
  containersRender: [], // ✅ state OR soft-draft view
  setContainers: () => {},
  instances: [],
  setInstances: () => {},

  // creators
  addContainer: () => {},
  addInstanceToContainer: () => {},

  // handlers (App-owned)
  handleDragStart: () => {},
  handleDragOver: () => {},     // ✅
  handleDragEnd: () => {},
  handleDragCancel: () => {},
});
