import React from "react";
import { useDraggable } from "@dnd-kit/core";
import { CSS } from "@dnd-kit/utilities";

export default function Instance({ id, label, dragging = false, overlay = false }) {
  const draggable = useDraggable({ id, disabled: overlay }); // overlay should NOT be draggable
  const { attributes, listeners, setNodeRef, transform, isDragging } = draggable;

  const style = overlay
    ? {}
    : { transform: CSS.Transform.toString(transform) };

  const className =
    "dnd-instance" + (isDragging ? " dnd-instance--dragging" : "");

  return (
    <div
      ref={overlay ? null : setNodeRef}
      className={className}
      style={{
        ...style,
        opacity: dragging && !overlay ? 0 : 1, // hide original while overlay is shown
      }}
      {...(!overlay ? listeners : {})}
      {...(!overlay ? attributes : {})}
    >
      {label}
    </div>
  );
}
