import React, { useContext, useMemo, useRef, useState, useEffect } from "react";
import {
  useDndContext,
  useDroppable,
  DndContext,
  DragOverlay,
  PointerSensor,
  useSensor,
  useSensors,
  TouchSensor,
  pointerWithin,
  rectIntersection,
} from "@dnd-kit/core";

// (you said you don't want snapCenterToCursor behavior)
import {
  SortableContext,
  rectSortingStrategy,
  verticalListSortingStrategy,
} from "@dnd-kit/sortable";

import Panel from "./Panel";
import PanelClone from "./PanelClone";
import { GridDataContext } from "./GridDataContext";
import { GridActionsContext } from "./GridActionsContext";

/* ------------------------------------------------------------
   DROPPABLE GRID CELL
   NOTE: highlight is driven by hoverCell (cursor-based),
         not isOver (rect-based)
------------------------------------------------------------ */
function CellDroppable({ r, c, dark, hoverCell, isPanelDrag }) {
  const { setNodeRef } = useDroppable({
    id: `cell-${r}-${c}`,
    data: { role: "grid:cell", row: r, col: c },
  });

  const highlight = isPanelDrag && hoverCell?.row === r && hoverCell?.col === c;

  return (
    <div
      ref={setNodeRef}
      style={{
        background: highlight
          ? "rgba(50,150,255,0.45)"
          : dark
          ? "#22272B"
          : "#2C333A",
        border: "1px solid #3F444A",
        transition: "background 80ms",
        pointerEvents: "auto",
      }}
    />
  );
}

function GridInner({ components }) {
  const {
    state,
    containersRender,
    instances,
    activeId: globalActiveId,
    activeSize,
  } = useContext(GridDataContext);

  const {
    handleDragStart: handleDragStartProp,
    handleDragOver: handleDragOverProp,
    handleDragEnd: handleDragEndProp,
    handleDragCancel: handleDragCancelProp,
    useRenderCount,
    dispatch,
    updatePanel,
    updateGrid,
    addContainer,
    socket,
  } = useContext(GridActionsContext);

  const sensors = useSensors(
    useSensor(TouchSensor, {
      activationConstraint: { delay: 500, tolerance: 8 },
    }),
    useSensor(PointerSensor)
  );

  useRenderCount("Grid");

  const grid = state.grid;
  const gridId = grid?._id;
  const rows = grid?.rows ?? 1;
  const cols = grid?.cols ?? 1;

  const visiblePanels = state.panels.filter((p) => p.gridId === gridId);

  const gridRef = useRef(null);
  const [activeId, setActiveId] = useState(state.activeId);
  const [activeData, setActiveData] = useState(null);
  const [panelDragging, setPanelDragging] = useState(false);

  /* ------------------------------------------------------------
     kept (if you still use it elsewhere)
  ------------------------------------------------------------ */
  const panelDragLiveRef = useRef({ id: null, dx: 0, dy: 0 });

  /* ------------------------------------------------------------
     ✅ NEW: cursor tracking for panel drag
     - dragStartPointerRef: pointer at drag start (from the handle)
     - pointerRef: live pointer (computed using delta)
     - hoverCell: which cell cursor is currently in
  ------------------------------------------------------------ */
  const dragStartPointerRef = useRef({ x: 0, y: 0 });
  const pointerRef = useRef({ x: 0, y: 0 });
  const [hoverCell, setHoverCell] = useState(null);

  /* ------------------------------------------------------------
      GRID SIZE STATE
  ------------------------------------------------------------ */
  const ensureSizes = (arr, count) => {
    if (!Array.isArray(arr) || arr.length === 0) return Array(count).fill(1);
    if (arr.length === count) return arr;
    if (arr.length < count)
      return [...arr, ...Array(count - arr.length).fill(1)];
    return arr.slice(0, count);
  };

  const [colSizes, setColSizes] = useState(() => ensureSizes(grid.colSizes, cols));
  const [rowSizes, setRowSizes] = useState(() => ensureSizes(grid.rowSizes, rows));

  useEffect(() => setColSizes(ensureSizes(grid.colSizes, cols)), [grid.colSizes, cols]);
  useEffect(() => setRowSizes(ensureSizes(grid.rowSizes, rows)), [grid.rowSizes, rows]);

  useEffect(() => {
    if (gridRef.current) {
      gridRef.current.dataset.sizes = JSON.stringify({ colSizes, rowSizes });
    }
  }, [colSizes, rowSizes]);

  const colTemplate = colSizes.map((s) => `${s}fr`).join(" ");
  const rowTemplate = rowSizes.map((s) => `${s}fr`).join(" ");

  const collisionDetection = useMemo(() => {
    return (args) => {
      const activeRole = args.active?.data?.current?.role;

      // Panels: ONLY allow pointer hits on grid cells (fine to keep)
      if (activeRole === "panel") {
        const pointerHits = pointerWithin(args);

        const cellHits = pointerHits.filter((hit) => {
          const role = args.droppableContainers.find((d) => d.id === hit.id)
            ?.data?.current?.role;
          return role === "grid:cell";
        });

        return cellHits.length ? [cellHits[0]] : [];
      }

      // Instances / other drags
      return rectIntersection(args);
    };
  }, []);

  const getPanel = (id) => visiblePanels.find((p) => p.id === id);

  /* ------------------------------------------------------------
      POINTER → CELL
  ------------------------------------------------------------ */
  const getCellFromPointer = (clientX, clientY) => {
    const rect = gridRef.current.getBoundingClientRect();

    // clamp to grid bounds to avoid weirdness at edges/outside
    const clampedX = Math.max(rect.left, Math.min(clientX, rect.right - 0.0001));
    const clampedY = Math.max(rect.top, Math.min(clientY, rect.bottom - 0.0001));

    const relX = (clampedX - rect.left) / rect.width;
    const relY = (clampedY - rect.top) / rect.height;

    let col = 0;
    let row = 0;

    const totalCols = colSizes.reduce((a, b) => a + b, 0);
    let acc = 0;
    for (let i = 0; i < colSizes.length; i++) {
      acc += colSizes[i];
      if (relX < acc / totalCols) {
        col = i;
        break;
      }
      // if we're at the end, fall through to last
      col = colSizes.length - 1;
    }

    const totalRows = rowSizes.reduce((a, b) => a + b, 0);
    acc = 0;
    for (let i = 0; i < rowSizes.length; i++) {
      acc += rowSizes[i];
      if (relY < acc / totalRows) {
        row = i;
        break;
      }
      row = rowSizes.length - 1;
    }

    return { row, col };
  };

  const getColPosition = (i) => {
    const total = colSizes.reduce((a, b) => a + b, 0);
    const before = colSizes.slice(0, i + 1).reduce((a, b) => a + b, 0);
    return (before / total) * 100;
  };

  const getRowPosition = (i) => {
    const total = rowSizes.reduce((a, b) => a + b, 0);
    const before = rowSizes.slice(0, i + 1).reduce((a, b) => a + b, 0);
    return (before / total) * 100;
  };

  /* ------------------------------------------------------------
      DRAG START
  ------------------------------------------------------------ */
  const handleDragStart = (event) => {
    setActiveId(event.active.id);
    setActiveData(event.active.data.current);

    const data = event.active.data.current;

    if (data?.role === "panel") {
      panelDragLiveRef.current = { id: data.panelId, dx: 0, dy: 0 };
      setPanelDragging(true);

      // capture pointer at drag START (from the handle press)
      const e = event.activatorEvent;
      const x = e?.clientX ?? e?.touches?.[0]?.clientX;
      const y = e?.clientY ?? e?.touches?.[0]?.clientY;

      if (typeof x === "number" && typeof y === "number") {
        dragStartPointerRef.current = { x, y };
        pointerRef.current = { x, y };
        setHoverCell(getCellFromPointer(x, y));
      } else {
        // safe fallback
        dragStartPointerRef.current = { x: 0, y: 0 };
        pointerRef.current = { x: 0, y: 0 };
        setHoverCell(null);
      }

      return;
    }

    handleDragStartProp(event);
  };

  /* ------------------------------------------------------------
      ✅ DRAG MOVE — compute live cursor using delta
      (DO NOT use activatorEvent here; it never updates)
  ------------------------------------------------------------ */
  const handleDragMove = (event) => {
    const data = event.active.data.current;
    if (!data || data.role !== "panel") return;

    const start = dragStartPointerRef.current;
    const dx = event.delta?.x ?? 0;
    const dy = event.delta?.y ?? 0;

    const x = start.x + dx;
    const y = start.y + dy;

    pointerRef.current = { x, y };

    if (gridRef.current) {
      setHoverCell(getCellFromPointer(x, y));
    }
  };

  /* ------------------------------------------------------------
      SANITIZE PLACEMENT
  ------------------------------------------------------------ */
  const sanitizePanelPlacement = (panel, rows, cols) => ({
    ...panel,
    row: Math.max(0, Math.min(panel.row, rows - 1)),
    col: Math.max(0, Math.min(panel.col, cols - 1)),
    width: panel.width,
    height: panel.height,
  });

  /* ------------------------------------------------------------
      ✅ DRAG END — commit placement using cursor-based cell
  ------------------------------------------------------------ */
  const handleDragEnd = (event) => {
    const { active } = event;

    const data = active?.data?.current;

    setActiveId(null);

    if (!data) {
      setPanelDragging(false);
      setHoverCell(null);
      return;
    }

    if (data.role !== "panel") {
      setPanelDragging(false);
      setHoverCell(null);
      handleDragEndProp(event);
      return;
    }

    const panel = getPanel(active.id);
    if (!panel || !gridRef.current) {
      setPanelDragging(false);
      setHoverCell(null);
      panelDragLiveRef.current = { id: null, dx: 0, dy: 0 };
      return;
    }

    const { x, y } = pointerRef.current;
    if (typeof x !== "number" || typeof y !== "number") {
      setPanelDragging(false);
      setHoverCell(null);
      panelDragLiveRef.current = { id: null, dx: 0, dy: 0 };
      return;
    }

    const { row, col } = getCellFromPointer(x, y);
    const updated = sanitizePanelPlacement({ ...panel, row, col }, rows, cols);

    dispatch(updatePanel(updated));
    socket.emit("update_panel", { panel: updated, gridId });

    setPanelDragging(false);
    setHoverCell(null);
    panelDragLiveRef.current = { id: null, dx: 0, dy: 0 };
  };

  /* ------------------------------------------------------------
      ✅ DRAG CANCEL — clear hover state too
  ------------------------------------------------------------ */
  const handleDragCancel = (event) => {
    setActiveId(null);
    setPanelDragging(false);
    setHoverCell(null);
    panelDragLiveRef.current = { id: null, dx: 0, dy: 0 };
    handleDragCancelProp(event);
  };

  /* ------------------------------------------------------------
      GRID RESIZING (unchanged)
  ------------------------------------------------------------ */
  const resizePendingRef = useRef({
    rowSizes: null,
    colSizes: null,
  });

  const finalizeResize = () => {
    const pending = resizePendingRef.current;
    if (!pending.rowSizes && !pending.colSizes) return;
    if (!state.grid?._id) return;

    dispatch(
      updateGrid({
        _id: state.grid._id,
        rows: state.grid.rows,
        cols: state.grid.cols,
        rowSizes: pending.rowSizes ?? rowSizes,
        colSizes: pending.colSizes ?? colSizes,
      })
    );

    socket.emit("update_grid", {
      gridId: state.grid._id,
      grid: {
        _id: state.grid._id,
        rows: state.grid.rows,
        cols: state.grid.cols,
        rowSizes: pending.rowSizes ?? rowSizes,
        colSizes: pending.colSizes ?? colSizes,
      },
    });

    resizePendingRef.current = { rowSizes: null, colSizes: null };
  };

  const getGridWidth = () => gridRef.current?.clientWidth || 1;
  const getGridHeight = () => gridRef.current?.clientHeight || 1;

  const resizeColumn = (i, pixelDelta) => {
    const gridWidth = getGridWidth();
    setColSizes((sizes) => {
      const next = i + 1;
      if (next >= sizes.length) return sizes;

      const total = sizes.reduce((a, b) => a + b, 0);
      const frDelta = (pixelDelta / gridWidth) * total;

      const copy = [...sizes];
      copy[i] = Math.max(0.3, copy[i] + frDelta);
      copy[next] = Math.max(0.3, copy[next] - frDelta);

      resizePendingRef.current.colSizes = copy;
      return copy;
    });
  };

  const resizeRow = (i, pixelDelta) => {
    const gridHeight = getGridHeight();
    setRowSizes((sizes) => {
      const next = i + 1;
      if (next >= sizes.length) return sizes;

      const total = sizes.reduce((a, b) => a + b, 0);
      const frDelta = (pixelDelta / gridHeight) * total;

      const copy = [...sizes];
      copy[i] = Math.max(0.3, copy[i] + frDelta);
      copy[next] = Math.max(0.3, copy[next] - frDelta);

      resizePendingRef.current.rowSizes = copy;
      return copy;
    });
  };

  const getClientX = (e) => (e.touches ? e.touches[0].clientX : e.clientX);
  const getClientY = (e) => (e.touches ? e.touches[0].clientY : e.clientY);

  const startColResize = (e, i) => {
    e.preventDefault();
    let startX = getClientX(e);

    const move = (ev) => {
      ev.preventDefault();
      const currentX = getClientX(ev);
      const delta = currentX - startX;
      startX = currentX;
      resizeColumn(i, delta);
    };

    const stop = () => {
      window.removeEventListener("mousemove", move);
      window.removeEventListener("mouseup", stop);
      window.removeEventListener("touchmove", move);
      window.removeEventListener("touchend", stop);
      finalizeResize();
    };

    window.addEventListener("mousemove", move);
    window.addEventListener("mouseup", stop);
    window.addEventListener("touchmove", move);
    window.addEventListener("touchend", stop);
  };

  const startRowResize = (e, i) => {
    e.preventDefault();
    let startY = getClientY(e);

    const move = (ev) => {
      ev.preventDefault();
      const currentY = getClientY(ev);
      const delta = currentY - startY;
      startY = currentY;
      resizeRow(i, delta);
    };

    const stop = () => {
      window.removeEventListener("mousemove", move);
      window.removeEventListener("mouseup", stop);
      window.removeEventListener("touchmove", move);
      window.removeEventListener("touchend", stop);
      finalizeResize();
    };

    window.addEventListener("mousemove", move);
    window.addEventListener("mouseup", stop);
    window.addEventListener("touchmove", move);
    window.addEventListener("touchend", stop);
  };

  if (!grid?._id) return <div>Loading grid…</div>;

  const isPanelDrag = panelDragging;

  return (
    <DndContext
      sensors={sensors}
      collisionDetection={collisionDetection}
      onDragStart={handleDragStart}
      onDragMove={handleDragMove}
      onDragOver={handleDragOverProp}
      onDragEnd={handleDragEnd}
      onDragCancel={handleDragCancel}
    >
      <div
        style={{
          position: "absolute",
          inset: 0,
          background: "#1D2125",
          overflow: "hidden",
        }}
      >
        <div
          ref={gridRef}
          style={{
            position: "absolute",
            inset: 0,
            display: "grid",
            gridTemplateColumns: colTemplate,
            gridTemplateRows: rowTemplate,
            width: "100%",
            height: "93vh",
            overflow: "hidden",
            touchAction: "none",
            overscrollBehaviorY: "none",
          }}
        >
          {[...Array(rows)].map((_, r) =>
            [...Array(cols)].map((_, c) => {
              const dark = (r + c) % 2 === 0;
              return (
                <CellDroppable
                  key={`${r}-${c}`}
                  r={r}
                  c={c}
                  dark={dark}
                  hoverCell={hoverCell}
                  isPanelDrag={isPanelDrag}
                />
              );
            })
          )}

          {/* Vertical resizers */}
          {[...Array(cols - 1)].map((_, i) => (
            <div
              key={`col-resize-${i}`}
              onMouseDown={(e) => startColResize(e, i)}
              onTouchStart={(e) => startColResize(e, i)}
              style={{
                position: "absolute",
                top: 0,
                bottom: 0,
                left: `${getColPosition(i)}%`,
                width: 6,
                marginLeft: -3,
                cursor: "col-resize",
                zIndex: 50,
                background: "transparent",
              }}
            />
          ))}

          {/* Row resizers */}
          {[...Array(rows - 1)].map((_, i) => (
            <div
              key={`row-resize-${i}`}
              onMouseDown={(e) => startRowResize(e, i)}
              onTouchStart={(e) => startRowResize(e, i)}
              style={{
                position: "absolute",
                left: 0,
                right: 0,
                top: `${getRowPosition(i)}%`,
                height: 6,
                marginTop: -3,
                cursor: "row-resize",
                zIndex: 50,
                background: "transparent",
              }}
            />
          ))}

          {/* Panels */}
          {visiblePanels.map((p) => (
            <Panel
              key={p.id}
              panel={p}
              dispatch={dispatch}
              gridRef={gridRef}
              cols={cols}
              rows={rows}
              activeId={activeId}
              components={components}
              gridActive={panelDragging}
              addContainer={addContainer}
            />
          ))}
        </div>
      </div>

      {/* ✅ Overlay blocked off (prevents "clone + real panel") */}
      <DragOverlay dropAnimation={null} zIndex={100000}>
        {null}
      </DragOverlay>
    </DndContext>
  );
}

export default React.memo(GridInner);
