// state/actions.js

export const ActionTypes = {
  // -----------------------------
  // hydration
  // -----------------------------
  HYDRATE: "HYDRATE",         // legacy compatibility
  FULL_STATE: "FULL_STATE",   // preferred

  // -----------------------------
  // auth/user
  // -----------------------------
  SET_USER_ID: "SET_USER_ID",
  SET_GRID_ID: "SET_GRID_ID",

  // -----------------------------
  // grid/panels (optional for now)
  // -----------------------------
  SET_GRID: "SET_GRID",
  UPDATE_GRID: "UPDATE_GRID",
  SET_AVAILABLE_GRIDS: "SET_AVAILABLE_GRIDS",

  SET_PANELS: "SET_PANELS",
  UPDATE_PANEL: "UPDATE_PANEL",
  ADD_PANEL: "ADD_PANEL",

  // -----------------------------
  // your current containers/instances demo
  // -----------------------------
  ADD_CONTAINER: "ADD_CONTAINER",
  ADD_INSTANCE_TO_CONTAINER: "ADD_INSTANCE_TO_CONTAINER",

  SET_ACTIVE_ID: "SET_ACTIVE_ID",
  SET_ACTIVE_SIZE: "SET_ACTIVE_SIZE",

  SET_DEBUG_EVENT: "SET_DEBUG_EVENT",

  SET_CONTAINERS: "SET_CONTAINERS",
  SET_INSTANCES: "SET_INSTANCES",

  PATCH_CONTAINER_ITEMS: "PATCH_CONTAINER_ITEMS",
  PATCH_INSTANCE: "PATCH_INSTANCE",

  SOFT_TICK: "SOFT_TICK",
};

// ------------------------------------
// Action Creators
// ------------------------------------

// --- hydration ---
export const hydrate = (payload) => ({
  type: ActionTypes.HYDRATE,
  payload,
});

export const fullState = (payload) => ({
  type: ActionTypes.FULL_STATE,
  payload,
});

// --- auth/user ---
export const setUserId = (userId) => ({
  type: ActionTypes.SET_USER_ID,
  payload: { userId },
});

export const setGridId = (gridId) => ({
  type: ActionTypes.SET_GRID_ID,
  payload: { gridId },
});

// --- grid ---
export const setGrid = (grid) => ({
  type: ActionTypes.SET_GRID,
  payload: { grid },
});

export const updateGrid = (gridPatch) => ({
  type: ActionTypes.UPDATE_GRID,
  payload: { gridPatch },
});

export const setAvailableGrids = (availableGrids) => ({
  type: ActionTypes.SET_AVAILABLE_GRIDS,
  payload: { availableGrids },
});

// --- panels ---
export const setPanels = (panels) => ({
  type: ActionTypes.SET_PANELS,
  payload: { panels },
});

export const updatePanel = (panel) => ({
  type: ActionTypes.UPDATE_PANEL,
  payload: { panel },
});

export const addPanel = (panel) => ({
  type: ActionTypes.ADD_PANEL,
  payload: { panel },
});

// --- containers/instances (your current app) ---
export const addContainer = ({ id, label }) => ({
  type: ActionTypes.ADD_CONTAINER,
  payload: { id, label },
});

export const addInstanceToContainer = ({ containerId, instance }) => ({
  type: ActionTypes.ADD_INSTANCE_TO_CONTAINER,
  payload: { containerId, instance },
});

export const setActiveId = (activeId) => ({
  type: ActionTypes.SET_ACTIVE_ID,
  payload: { activeId },
});

export const setActiveSize = (activeSize) => ({
  type: ActionTypes.SET_ACTIVE_SIZE,
  payload: { activeSize },
});

export const setDebugEvent = (debugEvent) => ({
  type: ActionTypes.SET_DEBUG_EVENT,
  payload: { debugEvent },
});

export const setContainers = (containers) => ({
  type: ActionTypes.SET_CONTAINERS,
  payload: { containers },
});

export const setInstances = (instances) => ({
  type: ActionTypes.SET_INSTANCES,
  payload: { instances },
});

export const patchContainerItems = ({ containerId, items }) => ({
  type: ActionTypes.PATCH_CONTAINER_ITEMS,
  payload: { containerId, items },
});

export const patchInstance = (instance) => ({
  type: ActionTypes.PATCH_INSTANCE,
  payload: { instance },
});

export const softTick = () => ({
  type: ActionTypes.SOFT_TICK,
});
