import React, { useState } from "react";
import {
  DndContext,
  DragOverlay,
  PointerSensor,
  useSensor,
  useSensors,
} from "@dnd-kit/core";
import Container from "./Container";
import Instance from "./Instance";

export default function App() {
  const [instanceContainer, setInstanceContainer] = useState("left");
  const [activeId, setActiveId] = useState(null);
  const [activeSize, setActiveSize] = useState(null); // {width,height}

  const sensors = useSensors(
    useSensor(PointerSensor, {
      activationConstraint: { distance: 5 },
    })
  );

  function handleDragStart(event) {
    setActiveId(event.active.id);

    // lock overlay size to the element’s initial rect
    const rect = event.active.rect?.current?.initial;
    if (rect) setActiveSize({ width: rect.width, height: rect.height });
  }

  function handleDragEnd(event) {
    const { over } = event;
    setActiveId(null);
    setActiveSize(null);

    if (!over) return;
    setInstanceContainer(over.id);
  }

  function handleDragCancel() {
    setActiveId(null);
    setActiveSize(null);
  }

  return (
    <div className="app-root">
      <div className="dnd-page">
        <h2 style={{ marginBottom: 16 }}>Basic dnd-kit Example</h2>

        <DndContext
          sensors={sensors}
          onDragStart={handleDragStart}
          onDragEnd={handleDragEnd}
          onDragCancel={handleDragCancel}
        >
          <div className="containers-row">
            <Container id="left" label="Left">
              {instanceContainer === "left" && (
                <Instance id="item-1" label="Drag me" dragging={activeId === "item-1"} />
              )}
            </Container>

            <Container id="right" label="Right">
              {instanceContainer === "right" && (
                <Instance id="item-1" label="Drag me" dragging={activeId === "item-1"} />
              )}
            </Container>
          </div>

          <DragOverlay adjustScale={false}>
            {activeId ? (
              <div
                style={{
                  width: activeSize?.width,
                  height: activeSize?.height,
                }}
              >
                <Instance id="overlay-item-1" label="Drag me" overlay />
              </div>
            ) : null}
          </DragOverlay>
        </DndContext>
      </div>
    </div>
  );
}
